import { routerReducer, RouterReducerState } from '@ngrx/router-store';
import { AuthReducer } from './login/auth.reducer';
import { AUTH_STATE_NAME } from './login/auth.selector';
import { AuthState } from './login/auth.state';
import { ProductsState } from './products/state/products.state';
import { productReducer } from './products/state/products.reducer';

export interface AppState {
//   [SHARED_STATE_NAME]: SharedState;
  [AUTH_STATE_NAME]: AuthState;
  router: RouterReducerState;
  products: ProductsState
}

export const appReducer = {
//   [SHARED_STATE_NAME]: SharedReducer,
  [AUTH_STATE_NAME]: AuthReducer,
  router: routerReducer,
  products: productReducer
};

export interface AuthResponseData {
    idToken: string;
    email: string;
    refreshToken: string;
    expiresIn: string;
    localId: string;
    registered?: boolean;
  }

import { User } from "./user.model";


export interface AuthState {
    user: User | null;
}

export const initialState : AuthState = {
    user: null,
}

export interface Product{
    id: string;
    name: string;
    description: string;
    price: number;
    image:string;
}

import { Product } from "../products.model";

export interface ProductsState{
    products: Product[];
}

export const initialState = {
    products : [
        // {id : '1', name: 'Product 1', price: '100$', desc: 'Product1 Description to be enter here.', },
        // {id : '2', name:'title2', price: '200$', description:'description2'},
        // {id : '3', name:'title3', price: '300$', description:'description3'},
        // {id : '4', name:'title4', price: '400$', description:'description4'},
    ]
}

import { RouterStateSerializer } from '@ngrx/router-store';
import { Params, RouterStateSnapshot } from '@angular/router';

export interface RouterStateUrl {
  url: string;
  params: Params;
  queryParams: Params;
}

export class CustomSerializer implements RouterStateSerializer<RouterStateUrl> {
  serialize(routerState: RouterStateSnapshot): RouterStateUrl {
    let route = routerState.root;

    while (route.firstChild) {
      route = route.firstChild;
    }

    const {
      url,
      root: { queryParams },
    } = routerState;
    const { params } = route;

    // Only return an object including the URL, params and query params
    // instead of the entire snapshot
    return { url, params, queryParams };
  }
}

export interface SharedState {
  showLoading: boolean;
  errorMessage: string;
}

export const initialState: SharedState = {
  showLoading: false,
  errorMessage: '',
};

