    let itemsLength: number = itemsArray?.length;
    for(let i=0; i<itemsLength; i++){
      this.cartTotal = Number(this.cartTotal) + Number(itemsArray[i]["price"]); 
    }
  }

  removeItem(index){
    let updatedArray = [];
