import { postsReducer } from './posts/state/posts.reducer';
import { PostsState } from './posts/state/posts.state';
import { productsReducer } from './products/state/products.reducer';
import { ProductsState } from './products/state/products.state';

export interface AppState {
//   [SHARED_STATE_NAME]: SharedState;
  [AUTH_STATE_NAME]: AuthState;
  router: RouterReducerState;
  products: ProductsState,
  posts: PostsState
}

export const appReducer = {
//   [SHARED_STATE_NAME]: SharedReducer,
  [AUTH_STATE_NAME]: AuthReducer,
  router: routerReducer,
  products: productsReducer,
  posts: postsReducer
};

export interface AuthResponseData {
    idToken: string;
    email: string;
    refreshToken: string;
    expiresIn: string;
    localId: string;
    registered?: boolean;
  }

import { User } from "./user.model";


export interface AuthState {
    user: User | null;
}

export const initialState : AuthState = {
    user: null,
}

export interface Post{
    id: string;
    title: string;
    description: string;
}

import { Post } from "../posts.model";

export interface PostsState{
    posts: Post[];
}

export const initialState = {
    posts : [
        {id : '1', title:'title1', description:'description1'},
        {id : '2', title:'title2', description:'description2'}
    ]
}

export interface Products{
    // id: number,
    title: string,
    description: string
}

import { createEntityAdapter, EntityState } from "@ngrx/entity";
import { Products } from "../products.model";

export interface ProductsState extends EntityState<Products>{
    products: Products[]
    //count: 0
}
export const productsAdapter = createEntityAdapter<Products>({
    sortComparer: sortByName,
  });

  // export const initialState: ProductsState = productsAdapter.getInitialState({
  //   count: 0,
  // });

export const initialState: ProductsState = productsAdapter.getInitialState({
  products : [
    {id: 1, title:'title1', description: 'description1'},
    {id: 2, title:'title2', description: 'description2'}
  ]
});

export function sortByName(a: Products, b: Products): number {
  const compare = a.title.localeCompare(b.title);
  if (compare > 0) {
    return -1;
  }

  if (compare < 0) {
    return 1;
  }

  return compare;
}

import { RouterStateSerializer } from '@ngrx/router-store';
import { Params, RouterStateSnapshot } from '@angular/router';

export interface RouterStateUrl {
  url: string;
  params: Params;
  queryParams: Params;
}

export class CustomSerializer implements RouterStateSerializer<RouterStateUrl> {
  serialize(routerState: RouterStateSnapshot): RouterStateUrl {
    let route = routerState.root;

    while (route.firstChild) {
      route = route.firstChild;
    }

    const {
      url,
      root: { queryParams },
    } = routerState;
    const { params } = route;

    // Only return an object including the URL, params and query params
    // instead of the entire snapshot
    return { url, params, queryParams };
  }
}

export interface SharedState {
  showLoading: boolean;
  errorMessage: string;
}

export const initialState: SharedState = {
  showLoading: false,
  errorMessage: '',
};

