import { Injectable } from "@angular/core";
import { Router } from "@angular/router";
import { Actions, createEffect, ofType } from "@ngrx/effects";
import { Store } from "@ngrx/store";
import { ToastrModule, ToastrService } from "ngx-toastr";
import { of } from "rxjs";
import { catchError, exhaustMap, map, tap } from "rxjs/operators";
import { AppState } from "../app.state";
import { setErrorMessage, } from "../store/Shared/shared.actions";
import { loginStart, loginSuccess, signupStart, signupSuccess } from "./auth.actions";
import { AuthService } from "./auth.service";

@Injectable()
export class AuthEffects {
  constructor(
    private actions$: Actions,
    private authService: AuthService,
    private store: Store<AppState>,
    private router: Router,
    private toastr: ToastrService
  ) {}

  login$ = createEffect(() => {
    return this.actions$.pipe(
      ofType(loginStart),
      exhaustMap((action) => {
        return this.authService.login().pipe( //action.email, action.password
          map((data:any) => {
            // this.store.dispatch(setLoadingSpinner({ status: false }));
            this.store.dispatch(setErrorMessage({ message: '' }));
            const user = this.authService.formatUser(data);
            this.authService.setUserInLocalStorage(user);
            return loginSuccess({ user, redirect: true });
          }),
          catchError((errResp) => {
            //this.store.dispatch(setLoadingSpinner({ status: false }));
            const errorMessage = this.authService.getErrorMessage(
              errResp.error.error.message
            );
            return of(setErrorMessage({ message: errorMessage }));
          })
        );
      })
    );
  });

  loginRedirect$ = createEffect(
    () => {
      return this.actions$.pipe(
        ofType(...[loginSuccess]),
        tap((action) => {
          this.store.dispatch(setErrorMessage({ message: '' }));
          if (action.redirect) {
            this.toastr.success('Login Success');
            this.router.navigate(['/']);
            
          }else{
            this.toastr.error('Login Failed');
          }
        })
      );
    },
    { dispatch: false }
  );

  signupRedirect$ = createEffect(
    () => {
      return this.actions$.pipe(
        ofType(...[signupSuccess]),
        tap((action) => {
          this.router.navigate(['/']);
        })
      );
    },
    { dispatch: false }
  );

  signUp$ = createEffect(() => {
    return this.actions$.pipe(
      ofType(signupStart),
      exhaustMap((action) => {
        return this.authService.signUp(action.firstname, 
          action.lastname, 
          action.contact,
          action.email, 
          action.password,
          action.retype_password).pipe(
          map((data:any) => {
            //this.store.dispatch(setLoadingSpinner({ status: false }));
            const user = this.authService.formatUser(data);
            this.authService.setUserInLocalStorage(user);
            return signupSuccess({ user });
          }),
          // catchError((errResp) => {
          //  // this.store.dispatch(setLoadingSpinner({ status: false }));
          //   const errorMessage = this.authService.getErrorMessage(
          //     errResp.error.error.message
          //   );
          //   return of(setErrorMessage({ message: errorMessage }));
          // })
        );
      })
    );
  });
}

import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { AuthResponseData } from './AuthResponseDate.model';
import { environment } from 'src/environments/environment';
import { User } from './user.model';
import { Store } from '@ngrx/store';
import { AppState } from '../app.state';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class AuthService {
  timeoutInterval: any;

  constructor(private http: HttpClient, private store: Store<AppState>) { }

  signUp(firstname:string,lastname:string,contact:string,email:string,password:string,retype_password:string){
    const params={
      firstname: firstname,
      lastname: lastname, 
      contact: contact, 
      email: email, 
      password: password, 
      retype_password: retype_password
    };
    console.log(params);
    return this.http.post(`http://localhost:3000/users`, params);
  }

  login(){
    // return this.http.post<AuthResponseData>(
    //   `https://identitytoolkit.googleapis.com/v1/accounts:signInWithPassword?key=${environment.FIRBASE_API_KEY}`,
    //   { email, password, returnSecureToken: true }
    // );
    return this.http.get(`http://localhost:3000/users`);
  }

  formatUser(data: AuthResponseData) {
    const expirationDate = new Date(
      new Date().getTime() + +data.expiresIn * 1000
    );
    const user = new User(
      data.email,
      data.idToken,
      data.localId,
      expirationDate
    );
    return user;
  }
  getErrorMessage(message: string) {
    switch (message) {
      case 'EMAIL_NOT_FOUND':
        return 'Email Not Found';
      case 'INVALID_PASSWORD':
        return 'Invalid Password';
      case 'EMAIL_EXISTS':
        return 'Email already exists';
      default:
        return 'Unknown error occurred. Please try again';
    }
  }

  setUserInLocalStorage(user: User) {
    localStorage.setItem('userData', JSON.stringify(user));

    this.runTimeoutInterval(user);
  }

  runTimeoutInterval(user: User) {
    const todaysDate = new Date().getTime();
    const expirationDate = user.expireDate.getTime();
    const timeInterval = expirationDate - todaysDate;

    this.timeoutInterval = setTimeout(() => {
      //this.store.dispatch(autoLogout());
      //logout functionality or get the refresh token
    }, timeInterval);
  }

}

import {
    Injectable,
    ComponentFactoryResolver,
    ApplicationRef,
    Injector,
    EmbeddedViewRef,
    ComponentRef,
    Type
  } from '@angular/core';
  import { DialogModule } from './dialog.module';

  import { DialogConfig } from './dialog-config';
  import { DialogInjector } from './dialog-injector';
  import { DialogRef } from './dialog-ref';
import { DialogComponent } from './dialog/dialog.component';
  
  @Injectable({
    providedIn: DialogModule
  })
  export class DialogService {
    dialogComponentRef: ComponentRef<DialogComponent>;
  
    constructor(
      private componentFactoryResolver: ComponentFactoryResolver,
      private appRef: ApplicationRef,
      private injector: Injector
    ) {}
  
    public open(componentType: Type<any>, config: DialogConfig): DialogRef {
      const dialogRef = this.appendDialogComponentToBody(config);
  
      this.dialogComponentRef.instance.childComponentType = componentType;
  
      return dialogRef;
    }
  
    private appendDialogComponentToBody(config: DialogConfig): DialogRef {
      const map = new WeakMap();
      map.set(DialogConfig, config);
  
      const dialogRef = new DialogRef();
      map.set(DialogRef, dialogRef);
  
      const sub = dialogRef.afterClosed.subscribe(() => {
        // close the dialog
        this.removeDialogComponentFromBody();
        sub.unsubscribe();
      });
  
      const componentFactory = this.componentFactoryResolver.resolveComponentFactory(DialogComponent);
      const componentRef = componentFactory.create(new DialogInjector(this.injector, map));
      this.appRef.attachView(componentRef.hostView);
  
      const domElem = (componentRef.hostView as EmbeddedViewRef<any>).rootNodes[0] as HTMLElement;
      document.body.appendChild(domElem);
  
      this.dialogComponentRef = componentRef;
  
      this.dialogComponentRef.instance.onClose.subscribe(() => {
        this.removeDialogComponentFromBody();
      });
      
      return dialogRef;
    }
  
    private removeDialogComponentFromBody(): void {
      this.appRef.detachView(this.dialogComponentRef.hostView);
      this.dialogComponentRef.destroy();
    }

    public close(): void {
        this.dialogComponentRef.instance.onClose.subscribe(() => {
          this.removeDialogComponentFromBody();
        });
        
      }
  }

