  updateProduct$ = createEffect(():any => {
    return this.actions$.pipe(
      ofType(updateProduct),
      switchMap((action) => {
        return this.productsService.updateProduct(action.product).pipe(
          map((data) => {
            const updatedProduct: Update<Product> = {
              id: action.product.id,
              changes: {
